import { async, ComponentFixture, TestBed } from "@angular/core/testing";

import { HttpClientTestingModule } from "@angular/common/http/testing";
import { RouterTestingModule } from "@angular/router/testing";

import { MovieAddComponent } from "./movie-add.component";
import { MovieService } from "src/app/services/movie.service";
import { FormBuilder } from "@angular/forms";
import { NO_ERRORS_SCHEMA, DebugElement } from "@angular/core";
import { By } from "@angular/platform-browser";

fdescribe("MovieAddComponent", () => {
  let component: MovieAddComponent;
  let fixture: ComponentFixture<MovieAddComponent>;
  let service: MovieService;

  beforeEach(async(() => {
    TestBed.configureTestingModule({
      imports: [RouterTestingModule, HttpClientTestingModule],
      declarations: [MovieAddComponent],
      providers: [MovieService, FormBuilder],
      schemas: [NO_ERRORS_SCHEMA],
    }).compileComponents();
  }));

  beforeEach(() => {
    fixture = TestBed.createComponent(MovieAddComponent);
    component = fixture.componentInstance;
    service = TestBed.get(MovieService);
    fixture.detectChanges();
  });

  it("should create", () => {
    expect(component).toBeTruthy();
  });

  it("form invalid when empty", () => {
    expect(component.movieForm.valid).toBeFalsy();
  });

  it("if form is invalid, save button is disabled", () => {
    let submitEL: DebugElement = fixture.debugElement.query(By.css(".save"));
    expect(submitEL.nativeElement.disabled).toBeTruthy();
  });

  it("release date field validity", () => {
    let releaseDate = component.movieForm.controls["release"];
    expect(releaseDate.valid).toBeFalsy();
  });

  it(`should disable save button until all required fields be filled`, () => {
    spyOn(console, "log");

    component.movieForm.controls["title"].setValue("simpson");
    component.movieForm.controls["release"].setValue("01/10/2000");
    component.movieForm.controls["description"].setValue("movie description");
    expect(component.movieForm.valid).toBeFalsy();
  });

  it(`should enable save button when required fields be filled `, () => {
    spyOn(console, "log");
    expect(component.movieForm.valid).toBeFalsy();
    component.movieForm.controls["title"].setValue("simpson");
    component.movieForm.controls["release"].setValue("01/10/2000");
    component.movieForm.controls["image"].setValue(
      "data:image/gif;base64,"
    );
    expect(component.movieForm.valid).toBeTruthy();
  });
});
